mapper.select:
SELECT members.*, roles.name
FROM members, roles
WHERE members. id = roles.user_id and id = #{id}

SELECT members.*, roles. name role
FROM members, roles
WHERE members. id = roles.user_id and email = #{email}

mapper.update:
UPDATE members 
SET name = #{name}, password = #{password}, address = #{address}, phone_number = #{phoneNumber}
WHERE id = #{id}

UPDATE members
SET name = #{name}, password = #{password}, address = #{address}, phone_number = #{phoneNumber}
WHERE email = #{email}
